import React, { useState } from 'react';
import { Text, View, StyleSheet,
         Button, ImageBackground,
        TouchableOpacity} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

const MainScreen = ({ navigation }) => {
  const loginPress = () => {
    navigation.push('Login');
  };

  return (
    <ImageBackground source={require('../img/MainBackground.jpg')}
                       style={styles.background}>
        <Text style={styles.title}>
          asset labs.
        </Text>
        <View style={styles.buttonHandler}>
          <TouchableOpacity onPress={loginPress}>
            <View style={styles.buttonL}>
              <Text style={styles.textL}>Sign In</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={loginPress}>
            <View style={styles.buttonR}>
              <Text style={styles.textR}>Register</Text>
            </View>
          </TouchableOpacity>
          {/* <Button title="Register" /> */}
          {/* <Button title="Sign In" onPress={loginPress}/> */}
        </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
    justifyContent:'center'
  },
  // buttonHandler: {
  //   flexDirection: 'row',
  //   width: 317,
  //   height: 48,
  //   justifyContent: 'center',
  //   alignItems: 'center',
  //   position: 'absolute',
  //   bottom: 10,
  //   padding: 85,
  // },
  title: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 220,
    fontSize: 70,
    fontWeight: 'bold',
    color: 'white'
  },
  buttonL: {
    borderRadius: 8,
    paddingVertical: 14,
    paddingHorizontal: 10,
    backgroundColor: '#282828',
  },
  textL: { 
    color: 'white',
    fontWeight: 'bold',
    //textTransform: 'uppercase',
    fontSize: 16,
    flexDirection: 'row',
    textAlign: 'center',
    justifyContent: 'center'
  },
  textR: { 
    color: 'black',
    fontWeight: 'bold',
    //textTransform: 'uppercase',
    fontSize: 16,
    flexDirection: 'row',
    textAlign: 'center',
    justifyContent: 'center'
  },
  buttonR: {
    borderRadius: 8,
    paddingVertical: 14,
    paddingHorizontal: 10,
    backgroundColor: 'white',
  }
});

export default MainScreen;
