import React, { useState } from 'react';
import { Text, View, StyleSheet,
         ImageBackground, ScrollView,
         TouchableOpacity, TextInput} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

import Colors from '../components/Colors';
import BackButton from '../components/BackButton';

const DashboardScreen = ({ navigation }) => {
  const [search, setSearch] = useState('');

  const back = () => {
    navigation.pop();
  };

  return (
    <ImageBackground source={require('../img/MainBackground.jpg')}
                       style={styles.background}>
      <View style={styles.container}>
        <View style={styles.firstRow}>
          <Text style={styles.title}>
            My Assets. 
          </Text>
          <TouchableOpacity>
            <View style={styles.barsIcon}>
              <Text style={styles.barsText}>
                <Icon name='user-circle' size={50}/>
              </Text>
            </View>
          </TouchableOpacity>
        </View>
        <View style={styles.searchContainer}>
          <View style={styles.secondContainer}>
            <TextInput style={styles.placeHolder} 
              placeholder="Search"
              placeholderTextColor='silver'
              onChangeText={(val) => setSearch(val)}
            />
            <TouchableOpacity hitSlop={{top: 30, bottom: 30, left: 30, right: 30}}>
              <View style={styles.searchIcon}>
                <Text style={styles.searchText}>
                  <Icon name='search' size={30}/>
                </Text>
              </View>
            </TouchableOpacity>
          </View>
        </View>
        <View style={styles.totalContainer}>
          <View style={styles.firstContainer}>
            <View style={styles.totalAssets}>
              <Text style={styles.totalAssetsTitle}>
                Total Assets Currently Managed
              </Text>
              <Text style={styles.assetsNumber}>
                25          
              </Text>
            </View>
            <TouchableOpacity>
              <View style={styles.qrcode}>
                <Text style={styles.barsText}>
                  <Icon name='qrcode' size={50}/>
                </Text>
              </View>
            </TouchableOpacity>
          </View>
          <View style={styles.tableContainer}>
            <ScrollView style={styles.assetContainer}>
              <Text style={styles.item}>ARRI Alexa Mini LF                     24L</Text>
              <Text style={styles.serial}>S/N: 00000001</Text>
              <Text style={styles.item}>Matthews C-Stand                     42W</Text>
              <Text style={styles.serial}>S/N: 00000002</Text>
              <Text style={styles.item}>Aputure NOVA P300c                  47R</Text>
              <Text style={styles.serial}>S/N: 00000003</Text>
              <Text style={styles.item}>ARRI Alexa Mini LF                     24L</Text>
              <Text style={styles.serial}>S/N: 00000004</Text>
              <Text style={styles.item}>Matthews C-Stand                      42W</Text>
              <Text style={styles.serial}>S/N: 00000005</Text>
              <Text style={styles.item}>Aputure NOVA P300c                  47R</Text>
              <Text style={styles.serial}>S/N: 00000006</Text>
              <Text style={styles.item}>Aputure NOVA P300c                  47R</Text>
              <Text style={styles.serial}>S/N: 00000003</Text>
              <Text style={styles.item}>ARRI Alexa Mini LF                     24L</Text>
              <Text style={styles.serial}>S/N: 00000004</Text>
              <Text style={styles.item}>Matthews C-Stand                      42W</Text>
              <Text style={styles.serial}>S/N: 00000005</Text>
              <Text style={styles.item}>Aputure NOVA P300c                  47R</Text>
              <Text style={styles.serial}>S/N: 00000006</Text>
            </ScrollView>
          </View>
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
    justifyContent:'center'
  },
  container: {
    paddingBottom: 10,
    paddingRight: 10,
    paddingTop: 160,
    paddingLeft: 10
  },
  title: {
    height: 45,
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white',
  },
  searchContainer:{
    flex: 1,
    alignItems: 'center',
    paddingTop: 55,
  },
  placeHolder: {
    height: 45, 
    width: 300, 
    borderColor: 'silver', 
    borderRadius: 14, 
    borderWidth: 1,  
    backgroundColor: Colors.grey,
    //marginBottom: 20, 
    color: 'silver',
    paddingLeft: 20
  },
  backContainer: {
    paddingLeft: 25,
    paddingTop: 30
  },
  searchText: { 
    color: 'white',
  },
  secondContainer: 
  {
    flexDirection: 'row'
  },
  searchIcon: 
  {
    paddingLeft: 20,
    paddingTop: 10
  },
  barsIcon: 
  {
    paddingLeft: 120,
  },
  barsText: {
    color: 'white'
  },
  qrcode: {
    paddingLeft: 5
  },
  qrcodeText: {
    color: 'white',
    fontSize: 20
  },
  firstRow: {
    flexDirection: 'row'
  },
  totalAssets: {
    //paddingBottom: 50,
    backgroundColor: Colors.grey,
    borderRadius: 14,
    height: 120,
    width: 270,
  },
  totalAssetsTitle: {
    color: 'silver',
    fontSize: 18,
    //paddingLeft: 33,
    paddingTop: 10,
    alignSelf: 'center',
    //fontWeight: 'bold'
  },
  totalContainer: {
    paddingTop: 60,
  },
  firstContainer: {
    flexDirection: 'row',
  },
  assetsNumber: {
    color: 'white',
    fontSize: 50,
    //paddingLeft: 120,
    alignSelf: 'center',
    paddingTop: 8
  },
  assetContainer: {
    //paddingBottom: 50,
    backgroundColor: Colors.grey,
    borderRadius: 14,
    height: 410,
    width: 300
  },
  item: {
    color: 'white',
    fontSize: 18,
    paddingLeft: 20,
    paddingTop: 20,
    fontWeight: 'bold'
  },
  serial: {
    color: 'white',
    fontSize: 14,
    paddingLeft: 20,
    paddingTop: 2
  },
  tableContainer: {
    paddingTop: 19
  }
});

export default DashboardScreen;
