import React, { useState } from 'react';
import { Text, View, StyleSheet,
         ImageBackground,
         TouchableOpacity, TextInput} from 'react-native';

import BackButton from '../components/BackButton';
import Colors from '../components/Colors';
        
const ForgotPasswordScreen = ({navigation}) => {
  const back = () => {
    navigation.pop();
  };

  const resetPress = () => {
    navigation.push('RegisterScreen');
  };

  return (
    <ImageBackground source={require('../img/MainBackground.jpg')}
                       style={styles.background}>
      <TouchableOpacity onPress={back}>
        <View style={styles.backContainer}>
          <BackButton></BackButton>
        </View>
      </TouchableOpacity>
      <View style={styles.titleContainer}>
        <Text style={styles.titleText}>
          Forgot password.
        </Text>
      </View>
      <View style={styles.subtitleContainer}>
        <Text style={styles.subtitleText}>
          Let's try to get you logged in
        </Text>
      </View>
      <View style={styles.subtitleTwoContainer}>
        <Text style={styles.subtitleTwoText}>
          Use the email you created your Asset Labs
          {"\n"} account with to reset your password.
        </Text>
      </View>
      <TouchableOpacity /*onPress={}*/>
          <View style={styles.sendCode}>
            <View style={styles.sendButton}>
              <Text style={styles.sendText}>Send Code</Text>
            </View>
          </View>
        </TouchableOpacity>
      <View style={styles.inputContainer}>
        <TextInput style={styles.placeHolder} 
          placeholder="Email Verification"
          placeholderTextColor='silver'
        />
        <TouchableOpacity /*onPress={registerPress}*/>
          <View style={styles.newUser}>
            <Text style={styles.text}>
              Don't have an account? 
              <Text style={styles.registerText}>
                {" "}Register
              </Text>
            </Text>
          </View>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
  },
  titleText: {
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white'
  },
  titleContainer: {
    paddingLeft: 25,
    paddingTop: 5
  },
  subtitleText: {
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: 24,
    color: 'white'
  },
  subtitleContainer: {
    paddingLeft: 25,
    paddingTop: 5
  },
  subtitleTwoText: {
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: 14,
    color: 'white'
  },
  subtitleTwoContainer: {
    paddingLeft: "50%",
    paddingTop: 5
  },
  backContainer: {
    paddingLeft: 25,
    paddingTop: 30
  },
  inputContainer:{
    flex: 1,
    alignItems: 'center',
    paddingTop: 60,
  },
  placeHolder: {
    height: 45, 
    width: 317, 
    borderColor: 'silver', 
    borderRadius: 14, 
    borderWidth: 1,  
    backgroundColor: Colors.grey,
    marginBottom: 20, color: 'silver',
    paddingLeft: 20
  },
  sendCode: {
    height: 45,
    width: 317,
    borderRadius: 14,
    paddingVertical: 12,
    paddingHorizontal: 10,
    backgroundColor: 'white',
  },
  sendText: { 
    color: 'black',
    //fontWeight: 'bold',
    fontSize: 17,
    flexDirection: 'row',
    textAlign: 'center',
    justifyContent: 'center',
    alignSelf: 'center', 
  },
  sendButton: {
    //paddingTop: 15
  },
  text: { 
    color: 'silver',
    //fontWeight: 'bold',
    fontSize: 14,
    flexDirection: 'row',
    textAlign: 'center',
    justifyContent: 'center',
    alignSelf: 'center', 
  },
  forgotPress: {
    paddingTop: 240,
    paddingBottom: 4,
  },
  newUser: {
    paddingBottom: 2,
  },
  registerText: {
    color: 'white',
    fontSize: 14,
    //flexDirection:
    textAlign: 'center',
    justifyContent: 'center',
    alignSelf: 'center',
  }
});

export default ForgotPasswordScreen;
